mapper.select:
SELECT account
FROM user
WHERE account = #{account}

SELECT email
FROM user
WHERE email = #{email}

mapper.update:
UPDATE user
name = #{name}, email = #{email}, phone = #{phone}, password = #{password}, updated_time = now()
WHERE id = #{id}

mapper.select:
SELECT reset_token,reset_token_expire
FROM user
WHERE reset_token = #{resetToken}
